function lorenzdyn(iter,i)%i=1 3D i=2 2Dclfhold offif (i==1)    t = 0:pi/50:0;    plot3(sin(t),cos(t),t)endhold onset(gca,'nextplot','replacechildren');X=zeros(1,iter);Y=X;Z=Y;X=2.9; Y=-1.3; Z=25.9;for i=1:iter    [X1,Y1,Z1]=nextiter(X,Y,Z);    line([X X1],[Y Y1],[Z Z1],'Color',[0 0 1]);    X=X1; Y=Y1; Z=Z1;   F(i) = getframe; % гальмо, щоб вимальовувати у реальному часыend%movie(F,1);hold off% Рівняння Lorenzоfunction [XO YO ZO]=nextiter(X,Y,Z)    dt=0.02;    s=10;    r=28;     b=8/3;    x1=X+s*(Y-X)*dt/2;	y1=Y+(X*(r-Z)-Y)*dt/2;	z1=Z+(X*Y-b*Z)*dt/2;    % update   % XO=x1;YO=y1;ZO=z1; % проста похідна	XO=X+s*(y1-x1)*dt;  %покращена похідна	YO=Y+(x1*(r-z1)-y1)*dt;	ZO=Z+(x1*y1-b*z1)*dt;